import React, { useState } from "react";
import './App.css';

function App() {
  const[position, setPosition] = useState(true)
  const[turn, setTurn] = useState("")

  const isSwitch = () => {
    setPosition(!position)

    if(position === true){
      setTurn("flex-start")
    }else{
      setTurn("flex-end")
    }
    console.log(position, turn)
  }
  return (
    <div className="App">     
      <div className="switch-outer" style={{justifyContent: turn }}>
        <div className="switch-inner"  onClick={isSwitch}>
        </div>
      </div>
    </div>
  );
}

export default App;
