import React, { useState } from "react";
import './App.css';

function App() {
  const[position, setPosition] = useState(true)
  const[turn, setTurn] = useState("")
  const[dot, setDot] = useState("")

  const isSwitch = () => {
    setPosition(!position)

    if(position === true){
      setTurn("flex-start")
      setDot("green")
    }else{
      setTurn("flex-end")
      setDot("gray")
    }

  }
  return (
    <div className="App">     
      <div className="switch-outer" style={{justifyContent: turn }}>
        <div className="switch-inner"  onClick={isSwitch}>
          <div style={{height: "4px", width: "13px", backgroundColor: dot, borderRadius: "5px"}}>
            <h3 style={{color: "white", fontSize: "100px"}}>START</h3>

          </div>
        </div> 
      </div>
    </div>
  );
}

export default App;
