import React, { useState } from "react";
import './App.css';

function App() {
  const[position, setPosition] = useState(true)
  const[turn, setTurn] = useState("")
  const[dot, setDot] = useState("")
  const[name, setName] = useState("")

  const isSwitch = () => {
    setPosition(!position)

    if(position === true){
      setTurn("flex-start")
      setDot("#1FFA09")
      setName("START")
    }else{
      setTurn("flex-end")
      setDot("gray")
      setName("STOP")
    }

  }
  return (
    <div className="App">     
      <div className="switch-outer" style={{justifyContent: turn }}>
        <div className="switch-inner"  onClick={isSwitch}>
          <div style={{height: "4px", width: "13px", backgroundColor: dot, borderRadius: "5px"}}></div>
          <h3 style={{color: "white", fontSize: "10px"}}>START</h3>
          <div className="on-icon-con" >
            <img src="https://cdn-icons-png.flaticon.com/128/4139/4139573.png" alt="" className="onIcon" />
          </div>
        </div> 
      </div>
    </div>
  );
}

export default App;
